                        setAppoinment(false);
                        setInventory(false);
                        setProvider(false);
                        setFacilities(false);
                      }}
                    />
                  ) : (
                    <Icon
                      size={23}
                      name="plus"
                      color="#009688"
                      onPress={() => {
                        setProduct(!product);
                        setAppoinment(false);
                        setInventory(false);
                        setProvider(false);
                        setFacilities(false);
                      }}
                    />
                  )}
                </View>
                {product ? (
                  <View
                    style={{
                      minheight: 200,
                      backgroundColor: '#FFF',
                      padding: 25,
                    }}>
                    <Text
                      style={{
                        fontWeight: '400',
                      }}>
                      Products Revenues
                    </Text>
                    {ProductsDetails &&
                      ProductsDetails.map(ele => {
                        return (
                          <View
                            style={{
                              backgroundColor: '#009688',
                              marginTop: 10,
                              padding: 15,
                              borderRadius: 5,
                            }}>
                            <Text
                              style={{
                                color: '#FFF',
                                fontSize: 12,
                                fontWeight: '500',
                              }}>
                              {' '}
                              counsumer : Mohmmad
                            </Text>
                            <Text
                              style={{
                                color: '#FFF',
                                fontSize: 12,
                                fontWeight: '500',
                              }}>
                              {' '}
                              Total : {ele.total} JD
                            </Text>
                            <Text
                              style={{
                                color: '#FFF',
                                fontSize: 12,
                                fontWeight: '500',
                              }}>
                              {' '}
                              Last Paid : {ele.paid_total} JD
                            </Text>
                          </View>
                        );
                      })}
                  </View>
                ) : null}
                {/* End Expenses */}
              </View>
            </View>
          ) : null}
          {/*  ------------------------------------------------------- */}
          <View
            style={{
              marginTop: 20,
              paddingHorizontal: 20,
              flexDirection: 'row',
              alignItems: 'center',
              justifyContent: 'space-between',
            }}>
            <Text style={{color: 'black', fontWeight: 'bold'}}>
              View List of Service Profit Breakdown
            </Text>
            {togle3 ? (
              <Icon
                size={30}
                name="minus"
                color="#009688"
                onPress={() => {
                  setTogle(false);
                  setTogle2(false);
                  setTogle3(!togle3);
                  setminus(false);
                }}
              />
            ) : (
              <Icon
                size={30}
                name="plus"
                color="#009688"
                onPress={() => {
                  setTogle(false);
                  setTogle2(false);
                  setTogle3(!togle3);
                }}
              />
            )}
            {/* <Icon
            size={30}
            name="plus"
            color="#009688"
            onPress={() => {
              setTogle(false);
              setTogle2(false);
              setTogle3(!togle3);
            }}
          /> */}
          </View>
          {togle3 ? (
            <View style={{height: 200, backgroundColor: '#009688'}}></View>
          ) : null}
        </View>
      </View>
    </ScrollView>
  );
}
const style = StyleSheet.create({
  container: {
    flex: 1,
  },
  viewCashRegistry: {
    marginTop: 15,
    backgroundColor: '#D8F0E7',
    padding: 15,
    width: '100%',
    borderRadius: 5,
  },
  filter: {
    color: 'green',
  },
});
