import React, { useState, useEffect } from 'react';
import {
  ScrollView,
  View,
  StyleSheet,
  Text,
  FlatList,
  TouchableOpacity,
  SafeAreaView,
  Modal,
  Pressable,
  TextInput,
} from 'react-native';
import { RadioButton } from 'react-native-paper';
import Icon from '@expo/vector-icons/FontAwesome';
import { useDispatch, useSelector } from 'react-redux';
import {
  getTaskas,
  getTodayTasks,
  getAllTasks,
  getTomorrowTasks,
  getThisWeek,
  getLastWeek,
} from '../store-tasks';
import { requestBuilder } from '../requestBuilder';
import Task from './Task';
import axios from 'axios';

export default function TasksLandingPage({ navigation }) {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('assigned');
  const [checked, setChecked] = useState('All');

  const dispatch = useDispatch();

  ////////
  const state = useSelector((state) => {
    return {
      token: state.tasks.token,
      // user_id: state.tasks.user_id,
      // user_name: state.tasks.user_name,
      user_id: state.dashboard.userToken.userId,
      user_name: `${state.dashboard.userToken.firstName} ${state.dashboard.userToken.lastName}`,
      tasks: state.tasks.tasks,
      userToken: state.dashboard.userToken,
    };
  });
  ///////  userId   ////    firstName    ///   lastName

  useEffect(() => {
    if (state.user_id) {
      getAssigned();
    }
  }, [state.user_id]);

  useEffect(() => {
    setModalVisible(false);
    if (checked === 'All') {
      dispatch(getAllTasks());
    } else if (checked === 'Today') {
      dispatch(getTodayTasks());
    } else if (checked === 'Tomorrow') {
      dispatch(getTomorrowTasks());
    } else if (checked === 'This week') {
      dispatch(getThisWeek());
    } else if (checked === 'Last week') {
      dispatch(getLastWeek());
    }
  }, [checked]);

  const getAssigned = async () => {
    try {
      // let data = await axios({
      //   method: 'POST',
      //   url: 'https://api.development.agentsoncloud.com/external/public/',
      //   headers: {
      //     'requsted-service': 'tasks',
      //     'requsted-path': '/tasks/assignedToMe/:id',
      //     'requsted-method': 'get',
      //   },
      //   data: {
      //     id: state.user_id,
      //   },
      // });
      const data = await axios(
        requestBuilder('tasks', '/tasks/assignedToMe/:id', 'get', {
          id: state.user_id,
        })
      );
      data.data.forEach((element) => {
        element.created =
          element.created_date.split('-').reverse().join('-') +
          ' at ' +
          element.created_time;
        element.deadline =
          element.due_date.split('-').reverse().join('-') +
          ' at ' +
          element.due_time;
      });
      dispatch(getTaskas(data.data));
    } catch (error) {
      console.log(error);
    }
  };
  const getGeneral = async () => {
    try {
      // const data = await axios.get(
      //   `http://10.0.2.2:30122/tasks/generalTasks/${state.user_id}`
      // );
      const data = await axios(
        requestBuilder('tasks', '/tasks/generalTasks/:id', 'get', {
          id: state.user_id,
        })
      );
      data.data.forEach((element) => {
        element.created =
          element.created_date.split('-').reverse().join('-') +
          ' at ' +
          element.created_time;
        element.deadline =
          element.due_date.split('-').reverse().join('-') +
          ' at ' +
          element.due_time;
      });
      dispatch(getTaskas([...data.data]));

      // const res = await axios.get('http://10.0.2.2:30122/tasks/allGeneralTasks/');
      const res = await axios(
        requestBuilder('tasks', '/tasks/allGeneralTasks', 'get')
      );
      res.data.forEach((element) => {
        element.created =
          element.created_date.split('-').reverse().join('-') +
          ' at ' +
          element.created_time;
        element.deadline =
          element.due_date.split('-').reverse().join('-') +
          ' at ' +
          element.due_time;
      });
      dispatch(getTaskas([...data.data, ...res.data]));
    } catch (error) {
      console.log('getGeneral', error.message);
    }
  };

  const getCreated = async () => {
    try {
      // const data = await axios.get(
      //   `http://10.0.2.2:30122/tasks/myTasks/${state.user_id}`
      // );
      const data = await axios(
        requestBuilder('tasks', '/tasks/myTasks/:id', 'get', {
          id: state.user_id,
        })
      );
      data.data.forEach((element) => {
        element.created =
          element.created_date.split('-').reverse().join('-') +
          ' at ' +
          element.created_time;
        element.deadline =
          element.due_date.split('-').reverse().join('-') +
          ' at ' +
          element.due_time;
      });
      // dispatch(getTaskas([...data.data]));

      // const res = await axios.get(
      //   `http://10.0.2.2:30122/tasks/myCompletedTasks/${state.user_id}`
      // );
      const res = await axios(
        requestBuilder('tasks', '/tasks/myCompletedTasks/:id', 'get', {
          id: state.user_id,
        })
      );
      res.data.forEach((element) => {
        element.created =
          element.created_date.split('-').reverse().join('-') +
          ' at ' +
          element.created_time;
        element.deadline =
          element.due_date.split('-').reverse().join('-') +
          ' at ' +
          element.due_time;
      });
      dispatch(getTaskas([...data.data, ...res.data]));
    } catch (error) {
      console.log('error', error);
    }
  };

  const changeTab = (str) => {
    setSelectedCategory(str);
    setModalVisible(false);
    setChecked('All');
    if (str === 'assigned') {
      getAssigned();
    } else if (str === 'general') {
      getGeneral();
    } else if (str === 'created') {
      getCreated();
    }
  };

  const createTask = () => {
    navigation.navigate('createTask');
  };

  return (
    <ScrollView>
      <View style={style.container}>
        {/* <View>
          <TouchableOpacity style={style.filter} onPress={() => {
              setModalVisible(!modalVisible)
          }}>
            <View>
              <Text style={style.filterText}> Time Filter </Text>
            </View>
          </TouchableOpacity>
        </View> */}
        <View style={style.tabs}>
          <TouchableOpacity
            style={
              selectedCategory === 'assigned'
                ? [style.tabView, style.activeTab]
                : style.tabView
            }
            onPress={() => {
              changeTab('assigned');
            }}
          >
            <View style={style.tab}>
              <Text style={style.tabText}> Assigned to me </Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity
            style={
              selectedCategory === 'general'
                ? [style.tabView, style.activeTab]
                : style.tabView
            }
            onPress={() => {
              changeTab('general');
            }}
          >
            <View style={style.tab}>
              <Text style={style.tabText}> General Tasks </Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity
            style={
              selectedCategory === 'created'
                ? [style.tabView, style.activeTab]
                : style.tabView
            }
            onPress={() => {
              changeTab('created');
            }}
          >
            <View style={style.tab}>
              <Text style={style.tabText}> Created by me </Text>
            </View>
          </TouchableOpacity>
        </View>
        <View style={style.title}>
          <Text> number of tasks is : {state.tasks.length} </Text>
          <View style={style.icons}>
            <Text
              onPress={() => {
                setModalVisible(!modalVisible);
              }}
            >
              {' '}
              {checked}{' '}
            </Text>
            <Icon
              name="filter"
              size={30}
              color="#009688"
              onPress={() => {
                setModalVisible(!modalVisible);
              }}
            />
            <Icon name="plus" size={30} color="#009688" onPress={createTask} />
          </View>
        </View>
        <ScrollView horizontal={true} contentContainerStyle={{ flex: 1 }}>
          <FlatList
            style={{ flex: 1 }}
            data={state.tasks}
            renderItem={({ item }) => {
              return (
                <Task
                  task={item}
                  type={selectedCategory}
                  navigation={navigation}
                />
              );
            }}
            keyExtractor={(item) => item.id}
          />
        </ScrollView>
        <Modal animationType="slide" transparent={true} visible={modalVisible}>
          <View style={style.centeredView2}>
            <View style={style.modalView}>
              <Pressable
                style={style.button}
                onPress={() => setModalVisible(!modalVisible)}
              >
                <Text style={style.textStyle}>Close</Text>
              </Pressable>
              <Text style={style.modalText}>Select Time Filter</Text>
              <View>
                <RadioButton.Group
                  onValueChange={(value) => setChecked(value)}
                  value={checked}
                >
                  <RadioButton.Item
                    label="All"
                    value="All"
                    style={style.radio}
                  />
                  <RadioButton.Item
                    label="Today"
                    value="Today"
                    style={style.radio}
                  />
                  <RadioButton.Item
                    label="Tomorrow"
                    value="Tomorrow"
                    style={style.radio}
                  />
                  <RadioButton.Item
                    label="This week"
                    value="This week"
                    style={style.radio}
                  />
                  <RadioButton.Item
                    label="Last week"
                    value="Last week"
                    style={style.radio}
                  />
                </RadioButton.Group>
              </View>
            </View>
          </View>
        </Modal>
      </View>
    </ScrollView>
  );
}

const style = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  tabs: {
    marginTop: 10,
    marginBottom: 20,
    flexDirection: 'row',
  },
  tabView: {
    marginRight: 5,
    marginLeft: 5,
    marginTop: 5,
